var neteaseFilter = function(doc) {
	print("采集到网易娱乐新闻");
	// print(doc);

	if (doc) {
		var elements = doc.select("ul.cm_ul_round li");
		if (elements) {
			for (var i in elements) {
				var a = elements[i].child(0);
				if (a) {
					print(a.html() + "  " + a.attr("href"));
				}
			}
		}
	}
};
